  void access::object_traits_impl< ::db::order, id_pgsql >::
  bind (pgsql::bind* b,
        image_type& i,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    std::size_t n (0);

    // id
    //
    if (sk != statement_insert && sk != statement_update)
    {
      b[n].type = pgsql::bind::bigint;
      b[n].buffer = &i.id_value;
      b[n].is_null = &i.id_null;
      n++;
    }

    // customer_id
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.customer_id_value;
    b[n].is_null = &i.customer_id_null;
    n++;

    // product_id
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.product_id_value;
    b[n].is_null = &i.product_id_null;
    n++;

    // sum
    //
    b[n].type = pgsql::bind::double_;
    b[n].buffer = &i.sum_value;
    b[n].is_null = &i.sum_null;
    n++;

    // date
    //
    if (sk != statement_update)
    {
      b[n].type = pgsql::bind::timestamp;
      b[n].buffer = &i.date_value;
      b[n].is_null = &i.date_null;
      n++;
    }
  }

