  bool access::view_traits_impl< ::db::customer_order, id_pgsql >::
  grow (image_type& i,
        bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // id
    //
    t[0UL] = 0;

    // customer_name
    //
    if (t[1UL])
    {
      i.customer_name_value.capacity (i.customer_name_size);
      grew = true;
    }

    // product_name
    //
    if (t[2UL])
    {
      i.product_name_value.capacity (i.product_name_size);
      grew = true;
    }

    // sum
    //
    t[3UL] = 0;

    // date
    //
    t[4UL] = 0;

    return grew;
  }

  void access::view_traits_impl< ::db::customer_order, id_pgsql >::
  bind (pgsql::bind* b,
        image_type& i)
  {
    using namespace pgsql;

    pgsql::statement_kind sk (statement_select);
    ODB_POTENTIALLY_UNUSED (sk);

    std::size_t n (0);

    // id
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.id_value;
    b[n].is_null = &i.id_null;
    n++;

    // customer_name
    //
    b[n].type = pgsql::bind::text;
    b[n].buffer = i.customer_name_value.data ();
    b[n].capacity = i.customer_name_value.capacity ();
    b[n].size = &i.customer_name_size;
    b[n].is_null = &i.customer_name_null;
    n++;

    // product_name
    //
    b[n].type = pgsql::bind::text;
    b[n].buffer = i.product_name_value.data ();
    b[n].capacity = i.product_name_value.capacity ();
    b[n].size = &i.product_name_size;
    b[n].is_null = &i.product_name_null;
    n++;

    // sum
    //
    b[n].type = pgsql::bind::double_;
    b[n].buffer = &i.sum_value;
    b[n].is_null = &i.sum_null;
    n++;

    // date
    //
    b[n].type = pgsql::bind::timestamp;
    b[n].buffer = &i.date_value;
    b[n].is_null = &i.date_null;
    n++;
  }

  void access::view_traits_impl< ::db::customer_order, id_pgsql >::
  init (view_type& o,
        const image_type& i,
        database* db)
  {
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (db);

    // id
    //
    {
      long long int& v =
        o.id;

      pgsql::value_traits<
          long long int,
          pgsql::id_bigint >::set_value (
        v,
        i.id_value,
        i.id_null);
    }

    // customer_name
    //
    {
      ::std::string& v =
        o.customer_name;

      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_value (
        v,
        i.customer_name_value,
        i.customer_name_size,
        i.customer_name_null);
    }

    // product_name
    //
    {
      ::std::string& v =
        o.product_name;

      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_value (
        v,
        i.product_name_value,
        i.product_name_size,
        i.product_name_null);
    }

    // sum
    //
    {
      double& v =
        o.sum;

      pgsql::value_traits<
          double,
          pgsql::id_double >::set_value (
        v,
        i.sum_value,
        i.sum_null);
    }

    // date
    //
    {
      ::time_t& v =
        o.date;

      pgsql::value_traits<
          ::time_t,
          pgsql::id_timestamp >::set_value (
        v,
        i.date_value,
        i.date_null);
    }
  }

  access::view_traits_impl< ::db::customer_order, id_pgsql >::query_base_type
  access::view_traits_impl< ::db::customer_order, id_pgsql >::
  query_statement (const query_base_type& q)
  {
    query_base_type r (
      "SELECT "
      "\"orders\".\"id\", "
      "\"customers\".\"name\", "
      "\"products\".\"name\", "
      "\"orders\".\"sum\", "
      "\"orders\".\"date\" ");

    r += "FROM \"orders\"";

    r += " INNER JOIN \"customers\" ON";
    // From DbModels.h:52:32
    r += query_columns::order::customer_id == query_columns::customer::id;

    r += " INNER JOIN \"products\" ON";
    // From DbModels.h:52:91
    r += query_columns::order::product_id == query_columns::product::id;

    if (!q.empty ())
    {
      r += " ";
      r += q.clause_prefix ();
      r += q;
    }

    return r;
  }

